const express=require("express"),cors=require("cors"),app=express();app.use(cors());const port=process.env.PORT||5e3;app.get("/events",((e,s)=>{s.send([{id:0,type:"indoor walker",distance:100,distanceType:"Feet",time:"1:30",status:!0},{id:1,type:"run",distance:3,distanceType:"Miles",time:"1:60",status:!1},{id:2,type:"bike ride",distance:6,distanceType:"Miles",time:"2:60",status:!1}])})),app.listen(port,(()=>console.log("Server started on "+port)));