const express=require("express"),cors=require("cors"),port=process.env.PORT||5e3,app=express();app.use(cors()),app.use(express.json()),app.use(express.urlencoded());let events=[{id:"0",type:"indoor walker",distance:100,distanceType:"Feet",time:"1:30",status:!0},{id:"1",type:"run",distance:3,distanceType:"Miles",time:"1:60",status:!1},{id:"2",type:"bike ride",distance:6,distanceType:"Miles",time:"2:60",status:!1}];app.get("/events",((e,s)=>{s.send(events)})),app.post("/events",((e,s)=>{events.push(e.body),s.send(e.body)})),app.delete("/events/:id",((e,s)=>{events=events.filter((s=>s.id!==e.params.id)),s.send(events)})),app.put("/events/:id",((e,s)=>{events=events.map((s=>s.id===e.params.id?{...s,status:!s.status}:s)),s.send(events)})),app.listen(port,(()=>console.log("Server started on "+port)));